            havakalitesi[i] = textBox_havaKalitesi.Text;
            nem[i] = textBox_nem.Text;
            basinc[i] = textBox_basinc.Text;

            string path = AppDomain.CurrentDomain.BaseDirectory + @"atmosferveritaban.json";
            Environment.SetEnvironmentVariable("GOOGLE_APPLICATION_CREDENTIALS", path);
            database = FirestoreDb.Create("atmosferveritaban");
            string dataname = DateTime.Now.ToLongDateString();
            string name = DateTime.Today.ToString() + " " + i.ToString();
            DocumentReference DOC = database.Collection("ucusverisi").Document("Bursa");
