            DocumentReference documentref = subCollectionRef.Document("Osmangazi");
            CollectionReference subCollectionRef2 = documentref.Collection("tarih");
            DocumentReference documentref2 = subCollectionRef2.Document("2842023");

            Dictionary<string, object> data1 = new Dictionary<string, object>()
