        private void harita()
        {
            //Double.TryParse(textBox_boylam.Text, out double boylam);
            //Double.TryParse(textBox_enlem.Text, out double enlem);
            Double enlem = Convert.ToDouble(textBox_enlem.Text);
            Double boylam = Convert.ToDouble(textBox_boylam.Text);
            string enlemtam = textBox_enlem.Text.Substring(0,2);
            int a = textBox_enlem.Text.Length - 1;
            string enlemkesir = textBox_enlem.Text.Substring(3, 6);

            Double enlemtam2 = Convert.ToDouble(enlemtam);
            Double enlemkesir2 = Convert.ToDouble(enlemkesir);

            double enlemkesir3 = enlemkesir2 * 0.000001;

            double enlemson = enlemtam2 + enlemkesir3;


            
            enlem = enlemson;


            string boylamtam = textBox_boylam.Text.Substring(0, 2);
            int b = textBox_boylam.Text.Length-1;
            string boylamkesir = textBox_boylam.Text.Substring(3, 6);

            double boylamtam2 = Convert.ToDouble(boylamtam);
            double boylamkesir2 = Convert.ToDouble(boylamkesir);

            double boylamkesir3 = boylamkesir2 * 0.000001;

            double boylamson = boylamtam2 + boylamkesir3;
            boylam = boylamson;

            int.TryParse(textBox_havaKalitesi.Text, out int havaKalitesi);



            map.DragButton = MouseButtons.Left;
            map.MapProvider = GMapProviders.GoogleMap;
            double lat = boylam;
            double lng = enlem;
            PointLatLng[] point_list = new PointLatLng[100];

            point_list[0].Lat = boylam;
            point_list[0].Lng = enlem;

            point_list[1].Lat = boylam + 0.00001;
            point_list[1].Lng = enlem + 0.00001;

            point_list[2].Lat = boylam + 2;
            point_list[2].Lng = enlem + 2;

            map.Position = new PointLatLng(enlem , boylam);
            map.MinZoom = 5;
            map.MaxZoom = 200;
            map.Zoom = 9;




            PointLatLng point = new PointLatLng(enlem, boylam);
            Bitmap ucak_1 = (Bitmap)Image.FromFile("ucak_1.png");
