            Bitmap ucak_6 = (Bitmap)Image.FromFile("ucak_6.png");
            Bitmap ucak_7 = (Bitmap)Image.FromFile("ucak_7.png");



            for (int x = 0; x < point_list.Length; x++)
            {

                if (havaKalitesi <= 50)
                {
                    GMapMarker marker = new GMarkerGoogle(point_list[x], ucak_1);
                    GMapOverlay markers = new GMapOverlay("markers");
                    markers.Markers.Add(marker);
                    map.Overlays.Add(markers);
                }
                else if (51 < havaKalitesi && havaKalitesi <= 100)
                {
                    GMapMarker marker = new GMarkerGoogle(point_list[x], ucak_2);
                    GMapOverlay markers = new GMapOverlay("markers");
                    markers.Markers.Add(marker);
                    map.Overlays.Add(markers);
                }
                else if (100 < havaKalitesi && havaKalitesi <= 150)
                {
                    GMapMarker marker = new GMarkerGoogle(point_list[x], ucak_3);
                    GMapOverlay markers = new GMapOverlay("markers");
                    markers.Markers.Add(marker);
                    map.Overlays.Add(markers);
                }
                else if (150 < havaKalitesi && havaKalitesi <= 200)
