                    GMapMarker marker = new GMarkerGoogle(point_list[x], ucak_4);
                    GMapOverlay markers = new GMapOverlay("markers");
                    markers.Markers.Add(marker);
                    map.Overlays.Add(markers);
                }

                else if (200 < havaKalitesi && havaKalitesi <= 300)
